DROP DATABASE IF EXISTS filmXtra;
CREATE DATABASE filmXtra;

USE filmXtra;

CREATE TABLE actor(
id INT UNSIGNED AUTO_INCREMENT,
pais VARCHAR(60) NOT NULL DEFAULT 'Apatrida',
fecha_nac DATE NOT NULL,
nombre VARCHAR(60) NOT NULL UNIQUE,
fecha_def DATE DEFAULT NULL,
nombre_real VARCHAR(60) NOT NULL DEFAULT 'Desconocido',
CONSTRAINT actor_pk PRIMARY KEY(id)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


CREATE TABLE genero(
id ENUM('Pelicula de culto', 'Accion', 'Animacion', 'Aventuras', 'Belico', 'Ciencia Ficcion', 'Cine Negro', 'Comedia', 'Documental','Drama', 'Fantastico', 'Infantil', 'Intriga','Musical', 'Romance', 'Serie de TV', 'Terror','Thriller', 'Western', 'Deportiva', 'Historica', 'Crimen', 'Policiaca', 'Religiosa', 'Cine mudo', 'Biografico', 'Erotico', 'Experimental', 'Propagandistico', 'Serie B', 'Road Movie', 'Cortometraje'), 
CONSTRAINT genero_pk PRIMARY KEY(id)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE poster(
id INT UNSIGNED AUTO_INCREMENT,
ruta VARCHAR(254) NOT NULL,
alt VARCHAR(1000) DEFAULT 'Poster de la película',
CONSTRAINT poster_pk PRIMARY KEY(id)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE obra_cine(
id INT UNSIGNED AUTO_INCREMENT,
titulo VARCHAR(200) NOT NULL,
titulo_original VARCHAR(200) NOT NULL,
pais VARCHAR(60) NOT NULL,
duracion NUMERIC(3) NOT NULL,
sinopsis TEXT NOT NULL,
año YEAR DEFAULT '1901',
productora VARCHAR(100) NOT NULL DEFAULT '-',
poster INT UNSIGNED DEFAULT NULL,
CONSTRAINT obra_cine_fk_poster FOREIGN KEY (poster) REFERENCES poster(id),
CONSTRAINT obra_cine_pk PRIMARY KEY(id)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE medio(
id INT UNSIGNED AUTO_INCREMENT,
web VARCHAR(100) NOT NULL DEFAULT 'desconocida' UNIQUE,
CONSTRAINT medio_pk PRIMARY KEY(id)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE personaje(
id INT UNSIGNED AUTO_INCREMENT,
orden ENUM('Principal', 'Secundario', 'Terciario'),
nombre VARCHAR(100) NOT NULL,
CONSTRAINT personaje_pk PRIMARY KEY(id)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE director(
id INT UNSIGNED AUTO_INCREMENT,
pais VARCHAR(60) NOT NULL DEFAULT 'Apatrida',
fecha_nac DATE DEFAULT '1888-10-14',
nombre VARCHAR(60) NOT NULL UNIQUE,
fecha_def DATE DEFAULT NULL,
CONSTRAINT director_pk PRIMARY KEY(id)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE festival(
id INT UNSIGNED AUTO_INCREMENT,  
año YEAR NOT NULL,
nombre VARCHAR(60) NOT NULL,
mejor_pelicula INT UNSIGNED NOT NULL,
CONSTRAINT festival_fk_obra_cine FOREIGN KEY(mejor_pelicula) REFERENCES obra_cine(id) ON DELETE CASCADE,
CONSTRAINT uc_nombre_mejor_pelicula_año UNIQUE (nombre, mejor_pelicula, año),
CONSTRAINT festival_pk PRIMARY KEY (id)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE usuario(
id INT UNSIGNED AUTO_INCREMENT,
nombre VARCHAR(100) NOT NULL,
apellido VARCHAR(100) NOT NULL,
contraseña VARCHAR(254) NOT NULL UNIQUE,
edad DATE DEFAULT '2000-01-01' NOT NULL,
email VARCHAR(100) NOT NULL UNIQUE,
pais VARCHAR(60) NOT NULL,
creado DATETIME DEFAULT CURRENT_TIMESTAMP,
modificado DATETIME DEFAULT CURRENT_TIMESTAMP,
CONSTRAINT usuario_pk PRIMARY KEY(id)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE tiene(
genero ENUM('Pelicula de culto', 'Accion', 'Animacion', 'Aventuras', 'Belico', 'Ciencia Ficcion', 'Cine Negro', 'Comedia', 'Documental','Drama', 'Fantastico', 'Infantil', 'Intriga','Musical', 'Romance', 'Serie de TV', 'Terror','Thriller', 'Western', 'Deportiva', 'Historica', 'Crimen', 'Policiaca', 'Religiosa', 'Cine mudo', 'Biografico', 'Erotico', 'Experimental', 'Propagandistico', 'Serie B', 'Road Movie', 'Cortometraje') NOT NULL,
CONSTRAINT tiene_fk_genero FOREIGN KEY(genero) REFERENCES genero(id),
obra_cine INT UNSIGNED,
CONSTRAINT tiene_fk_obra_cine FOREIGN KEY(obra_cine) REFERENCES obra_cine(id) ON DELETE CASCADE,
CONSTRAINT tiene_pk PRIMARY KEY(genero, obra_cine)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE creada_con_1(
personaje INT UNSIGNED,
actor INT UNSIGNED,
CONSTRAINT creada_con_1_fk_personaje FOREIGN KEY(personaje) REFERENCES personaje(id),
CONSTRAINT creada_con_1_fk_actor FOREIGN KEY(actor) REFERENCES actor(id),
CONSTRAINT creada_con_1_pk PRIMARY KEY(personaje, actor)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE creada_con_2(
personaje INT UNSIGNED,
obra_cine INT UNSIGNED,
CONSTRAINT creada_con_2_fk_personaje FOREIGN KEY(personaje) REFERENCES personaje(id),
CONSTRAINT creada_con_2_fk_obra_cine FOREIGN KEY(obra_cine) REFERENCES obra_cine(id),
CONSTRAINT creada_con_2_pk PRIMARY KEY(personaje, obra_cine)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE dirigida(
director INT UNSIGNED,
obra_cine INT UNSIGNED,
CONSTRAINT dirigida_fk_director FOREIGN KEY(director) REFERENCES director(id),
CONSTRAINT dirigida_fk_obra_cine FOREIGN KEY(obra_cine) REFERENCES obra_cine(id) ON DELETE CASCADE,
CONSTRAINT dirigida_pk PRIMARY KEY(director, obra_cine)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE critica_profesional(
id INT UNSIGNED AUTO_INCREMENT,
contenido TEXT NOT NULL,
autor VARCHAR(50) NOT NULL,
medio INT UNSIGNED,
obra_cine INT UNSIGNED NOT NULL,
CONSTRAINT critica_profesional_fk_medio FOREIGN KEY (medio) REFERENCES medio(id),
CONSTRAINT citica_profesional_fk_obra_cine FOREIGN KEY(obra_cine) REFERENCES obra_cine(id) ON DELETE CASCADE,
CONSTRAINT critica_profesional_pk PRIMARY KEY(id)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE secuelas(
id INT UNSIGNED AUTO_INCREMENT,
obra_cine INT UNSIGNED NOT NULL,
saga VARCHAR(200) NOT NULL,
orden INT UNSIGNED DEFAULT 1,
num_pelis INT UNSIGNED,
CONSTRAINT secuelas_pk PRIMARY KEY(id),
CONSTRAINT secuelas_fk_obra_cine FOREIGN KEY(obra_cine) REFERENCES obra_cine(id) ON DELETE CASCADE
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
-- orden = 0 significa que no forma parte del orden, posiblemente spin-off --

CREATE TABLE critica(
id INT UNSIGNED AUTO_INCREMENT,
obra_cine INT UNSIGNED NOT NULL,
usuario INT UNSIGNED NOT NULL,
critica TEXT NOT NULL,
creada DATETIME DEFAULT CURRENT_TIMESTAMP,
CONSTRAINT critica_fk_obra_cine FOREIGN KEY(obra_cine) REFERENCES obra_cine(id) ON DELETE CASCADE,
CONSTRAINT critica_fk_usuario FOREIGN KEY(usuario) REFERENCES usuario(id),
CONSTRAINT uc_critica_obra_usuario UNIQUE (obra_cine , usuario),
CONSTRAINT critica_pk PRIMARY KEY (id)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE evaluacion(
id INT UNSIGNED AUTO_INCREMENT,
evaluacion NUMERIC (2,1) NOT NULL,
obra_cine INT UNSIGNED NOT NULL,
usuario INT UNSIGNED NOT NULL,
CONSTRAINT evaluacion_fk_usuario FOREIGN KEY(usuario) REFERENCES usuario(id),
CONSTRAINT evaluacion_fk_obra_cine FOREIGN KEY(obra_cine) REFERENCES obra_cine(id) ON DELETE CASCADE,
CONSTRAINT uc_evalucion_obra_usuario UNIQUE (obra_cine, usuario),
CONSTRAINT evaluacion_pk PRIMARY KEY (id)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE likes(
usuario INT UNSIGNED,
critica INT UNSIGNED,
CONSTRAINT likes_fk_usuario FOREIGN KEY (usuario) REFERENCES usuario(id),
CONSTRAINT likes_fk_critica FOREIGN KEY (critica) REFERENCES critica(id) ON DELETE CASCADE,
CONSTRAINT likes_pk PRIMARY KEY(usuario, critica)
)ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;




